function textc:init(xpos, ypos, value, num)
	self.x = xpos
	self.y = ypos
	self.val = value
	self.num = num
end

function reset()
	board = newBoard()
	board = resetBoard(board)
	point = textc(1,1,1,1)
	gameover = textc(a,a,"THE GAME",false)
	highlight = textc(14,14,1,1)
	click = false
	piecestaken = {}
	piecestaken["x"] = 0
	piecestaken["o"] = 0
	teststr = "Test"
	turn = (turns[2] == 1 and math.random() < .5) and "o" or "x"
	aion = false
end

