function on.arrowKey(key)
	if mode == 2 and not gameover.num and not aion then
		if key == "up" then
			move(point,0,-1)
		elseif key == "left" then
			move(point,-1,0)
		elseif key == "down" then
			move(point,0,1)
		elseif key == "right" then
			move(point,1,0)
		end
		if point.x == 9 then
			point.x = 1
		elseif point.x == 0 then
			point.x = 8
		elseif point.y == 0 then
			point.y = 8
		elseif point.y == 9 then
			point.y = 1
		end
	init = true
	end

	if mode == 1 then
		if key == "up" then
			selector.y = selector.y - 15
			selector.val = selector.val - 1
		elseif key == "down" then
			selector.y = selector.y + 15
			selector.val = selector.val + 1
		end
		if selector.val == 0 then
			selector.y = 153
			selector.val = 3
		elseif selector.val == 4 then
			selector.y = 123
			selector.val = 1
		end
		init2 = true
		
		if selector.val == 1 and (key == "left" or key == "right") then
			if key == "left" then
				ai.num = ai.num - 1
			elseif key == "right" then
				ai.num = ai.num + 1
			end
			if ai.num == 0 then
				ai.num = 3
			elseif ai.num == 4 then
				ai.num = 1
			end
			if ai.num == 1 then
				ai.val = "2 Player"
			elseif ai.num == 2 then
				ai.val = "Easy"
			elseif ai.num == 3 then
				ai.val = "Normal"
			end
		end
	end

	platform.window:invalidate()
end

function on.enterKey()
	if mode == 2 and not gameover.num and not aion then
		if not click then
			xorig,yorig = point.x,point.y
			if string.lower(board[point.x][point.y]) == turn then
				shift(highlight,point.x,point.y)
				PTM = whereToMove(point.x,point.y)
				dots = {}
				if PTM then
					for j,place in ipairs(PTM) do
						table.insert(dots,textc(place[1],place[2],place[3],nil))
					end
				end
				click = true
			end		
		elseif click then
			--Deselecting
			if point.x == highlight.x and point.y == highlight.y then
				on.escapeKey()
			end

			for j, dot in ipairs(dots) do
				if point.x == dot.x and point.y == dot.y then
					movePiece(xorig,yorig,dot.x,dot.y,dot.val)
				end
			end
		end
		init = true
		platform.window:invalidate()
	end

	if mode == 1 then
		mode = selector.val + 1
		if mode == 2 then
			turns = {1,ai.num}
		end
		init2 = true
		platform.window:invalidate()
	end
end

function on.escapeKey()
	if mode == 2 and click then
		shift(highlight,14,14)
		dots = {}
		click = false
		init = true
	else	
		mode = 1
	end
	
	platform.window:invalidate()
end

function checkstale(t)
	num = 0
	for i=1,8 do
		for j=1,8 do
			if board[i][j] == t then 
				if not whereToMove(i,j) then
					num = num + 1
				end
			end
		end
	end
	return num
end

function on.tabKey()
	init = false
	platform.window:invalidate()
end

function gameovera(side)
	shift(gameover,100,100)
	color = side == "x" and "Black" or "White"
	gameover.val = color.." has won THE GAME!!"
	gameover.num = true
	platform.window:invalidate()
end

function AIchoose(diff,piece)
	if gameover.num == false then
		possmoves = {}
		original = {}
		for i=1,8 do
			for j=1,8 do
				if string.lower(board[i][j]) == piece then
					if whereToMove(i,j) ~= false then
						table.insert(original,{i,j})
						table.insert(possmoves,whereToMove(i,j))
					end
				end
			end
		end	

		if diff == "easy" then
			ab = math.random(#possmoves)
			selec1 = possmoves[ab]
			ac = math.random(#selec1)
			selecorig = original[ab]
			selec2 = selec1[ac]
		end

		jumpers = {}
		kingers = {}
		if diff == "normal" then
			for l,m in ipairs(possmoves) do
				for n,o in ipairs(m) do
					if o[3] == 1 then
						table.insert(jumpers,o)
						table.insert(jumpers,l)
					end
					if o[2] == 1 and board[original[l][1]][original[l][2]] ~= "O" then
						table.insert(kingers,o)
						table.insert(kingers,l)
					end
				end
			end
			if #kingers > 0 then
				q = math.random(#kingers/2)
				selec1 = {kingers[2*q-1],kingers[2*q]}
				selecorig = original[selec1[2]]
				selec2 = selec1[1]
			elseif #jumpers > 0 then
				q1 = math.random(#jumpers/2)
				selec1 = {jumpers[2*q1-1],jumpers[2*q1]}
				selecorig = original[selec1[2]]
				selec2 = selec1[1]
			else
				ab = math.random(#possmoves)
				selec1 = possmoves[ab]
				ac = math.random(#selec1)
				selecorig = original[ab]
				selec2 = selec1[ac]
			end
		end
		movePiece(selecorig[1],selecorig[2],selec2[1],selec2[2],selec2[3])
	end
end

function whereToMove(xstart, ystart)
	tile = board[xstart][ystart]
	if tile ~= " " then
		PTM = {}
		othertile = oppside(tile)
		
		if tile == "X" or tile == "O" then
			moves = {{1,1},{1,-1},{-1,-1},{-1,1}}
		elseif tile == "x" then
			moves = {{1,1},{-1,1}}
		elseif tile == "o" then
			moves = {{1,-1},{-1,-1}}
		end
		
		for a, move in ipairs(moves) do
			x = xstart
			y = ystart

			cx, cy  = move[1], move[2]
			
			x = x + cx
			y = y + cy

			if isOnBoard(x,y) and isEmpty(x,y) then
				table.insert(PTM,{x,y,0})
			elseif isOnBoard(x,y) and string.lower(board[x][y]) == othertile then
				x = x + cx
				y = y + cy
				if isOnBoard(x,y) and isEmpty(x,y) then
					table.insert(PTM,{x,y,1})
				end
			end
		end
		if #PTM == 0 then
			return false
		else
			return PTM
		end
	end
end

function drawPieces(brd,gc)
	for x=1,8 do
		for y=1,8 do
			side = brd[x][y]
			if side ~= " " then
--				gc:fillRect(60+20*x,5+20*y,10,10)
				gc:setColorRGB(50,50,50)
				gc:fillArc(60+20*x,5+20*y,14,14,0,360)
				if string.lower(side) == "x" then
					gc:setColorRGB(0,0,0)
				elseif string.lower(side) == "o" then
					gc:setColorRGB(240,240,240)
				end
				gc:fillArc(58+20*x,3+20*y,14,14,0,360)
				if side == "X" or side == "O" then
					gc:setColorRGB(180,155,30)
					gc:fillRect(63+20*x,8+20*y,5,5)
				end
			end
		end
	end
end

function newBoard()
	brd = {}
	for i=1,8 do
		table.insert(brd,{" "," "," "," "," "," "," "," "})
	end
	return brd
end

function resetBoard(brd)
	for x=1,8 do
		for y=1,8 do
			brd[x][y] = ' '
		end
	end

	for i=1,8 do
		for j=1,3 do
			if isOdd(i + j) then
				brd[i][j] = 'x'
			end
		end
	end

	for i=1,8 do
		for j=6,8 do
			if isOdd(i + j) then
				brd[i][j] = 'o'
			end
		end
	end
	return brd
end

function movePiece(xbegin,ybegin,xend,yend,jump)
	--Moving
	board[xbegin][ybegin], board[xend][yend] = " ",board[xbegin][ybegin]
	--Kinging
	if board[xend][yend] == "x" and yend == 8 then
		board[xend][yend] = "X"
	elseif board[xend][yend] == "o" and yend == 1 then
		board[xend][yend] = "O"
	end
	--Capturing a Piece
	if jump == 1 then
		board[(xbegin+xend)/2][(ybegin+yend)/2] = " "
		piecestaken[turn] = piecestaken[turn] + 1
		if piecestaken[turn] >= 12 then
			gameovera(turn)
		end
	end
	--Change turn
	turn = oppside(turn)
	currentturn = turn == "x" and turns[1] or turns[2]
	--Check stale
	if checkstale(turn) == 12 - piecestaken[oppside(turn)] then
		gameovera(oppside(turn))
	end	

	if currentturn == 1 then
		aion = false
	elseif currentturn > 1 then
		aion = true
		if currentturn == 2 then
			AIchoose("easy","o")
		elseif currentturn == 3 then
			AIchoose("normal","o")
		elseif currentturn == 4 then
			AIchoose("hard","o")
		end
	end
	on.escapeKey()
end

function drawBoard(gc)
	gc:setColorRGB(139,69,19)
	gc:fillRect(67,12,176,176)
	gc:setColorRGB(0,0,0)
	gc:drawRect(75,20,160,160)
	for i2=1,8 do
		for j2=1,8 do
			if isOdd(i2+j2) then
				gc:setColorRGB(210,105,30)
			else
				gc:setColorRGB(255,211,155)
			end
			gc:fillRect(56+20*i2,1+20*j2,19,19)
		end
		gc:setColorRGB(0,0,0)
		gc:drawLine(75+20*i2,20,75+20*i2,180)
		gc:drawLine(75,20+20*i2,235,20+20*i2)
	end
end

function isOnBoard(x,y)
	return x >= 1 and x <= 8 and y >= 1 and y<= 8
end

function isEmpty(x,y)
	return board[x][y] == " "
end

function isOdd(num)
	return num%2 == 1
end

function oppside(side)
	return string.lower(side) == "x" and "o" or "x"
end

function shift(obj,x,y)
	obj.x,obj.y = x,y
end

function move(obj,x,y)
	obj.x = obj.x + x
	obj.y = obj.y + y
end

function on.mouseDown(x,y)
	if mode == 2 and x>75 and x<235 and y>20 and y < 180 then
		point.x = math.ceil((x-75)/20)
		point.y = math.ceil((y-20)/20)
		on.enterKey()
		init = true
		platform.window:invalidate()
	end
end
