--sprites
local hunkyCrest = ""

--functions
local drawRectFill = zmg.drawRectFill
local fastCopy = zmg.fastCopy
local keyMenuFast = zmg.keyMenuFast
local clear = zmg.clear
local makeColor = zmg.makeColor
local drawText = zmg.drawText
local copySprite = zmg.copySprite
local floor = math.floor

--key variables
local keyExit = 47
local keyUp = 28
local keyDown = 37
local keyLeft = 38
local keyRight = 27

--other variables
local LCD_SCREEN_WIDTH = 384
local LCD_SCREEN_HEIGHT = 216

local angle = 0
local i = 1
local testVar1 = 83
local testVar2 = floor(LCD_SCREEN_HEIGHT / 3) + 3

--main loop
while keyMenuFast() ~= keyExit do
	clear()
	
	drawRectFill(0, 0, LCD_SCREEN_WIDTH, floor(LCD_SCREEN_HEIGHT / 3), makeColor(40, 130, 0))
	drawRectFill(0, floor(LCD_SCREEN_HEIGHT / 3), LCD_SCREEN_WIDTH, floor(LCD_SCREEN_HEIGHT / 3), makeColor(236, 236, 236))
	drawRectFill(0, floor(2 * LCD_SCREEN_HEIGHT / 3), LCD_SCREEN_WIDTH, floor(LCD_SCREEN_HEIGHT / 3), makeColor(132, 0, 0))
	
	copySprite(testVar2, testVar1, 27, 57, hunkyCrest)
	
	drawText(1,1,testVar1,0x0000,0xffff)
	drawText(1,15,testVar2,0x0000,0xffff)
	
	if keyMenuFast() == 28 and testVar1 > 0 then testVar1 = testVar1-1
		elseif keyMenuFast() == 37 then testVar1 = testVar1+1
		elseif keyMenuFast() == 38 and testVar2 > 0 then testVar2 = testVar2-1
		elseif keyMenuFast() == 27 then testVar2 = testVar2+1
	end
	
	angle = angle + i
	if angle == 150 then i = -1
		elseif angle == 0 then i = 1
	end
	fastCopy()
end
